import './App.css';
import './css/dashboard.css';
import { useState, useEffect } from 'react';
import { CSAPIInstance } from './classes/CounterStrikeApi';
import SearchBar from './components/search';
import OpenAIApiSingleton from './classes/OpenAIAPI';

function App() {

  const OPENAI_KEY = "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";
  const [itemName, setItemName] = useState<string>("");
  const [img, setImg] = useState<string>("");

  const [image, setImage] = useState<string>("");
  const [pricemed, setPriceMed] = useState<number>(0);
  const [exists, setExists] = useState<boolean>(false);
  const [searching, setSearching] = useState<boolean>(false);

  const [infoText, setInfoText] = useState<string>("");

  const [avg, setAvg] = useState(0);
  const [median, setMedian] = useState(0);
  const [price, setPrice] = useState(0);
  const [sold, setSold] = useState(0);
  const [createdat, setCreatedAt] = useState("");

  const search = (name: string) => {
    let img_: string = "";
    let text_: string = "N/A";
    let name_: string = "";

    let image_ = "";
    let pricemed_ = 0;
    let avg_ = 0;
    let median_ = 0;
    let price_ = 0;
    let sold_ = 0;
    let createdat_ = "";

    setSearching(true);


    CSAPIInstance.fetchItemStats(name)
      .then((val => {
        if (val != null) {

          image_ = val.image;
          pricemed_ = val.pricemedian;
          name_ = val.name;
          setItemName(name_);
          setImage(image_);
          setPriceMed(pricemed_);
          setExists(true);

          const info = OpenAIApiSingleton.getInstance(OPENAI_KEY).generateText(
            "Give me a blurb about this weapon in a professional tone as though you are a historian " 
            + name).then((val) => {
            setInfoText(val);
            setSearching(tr);
          });

        } else {
          setExists(false);
        }
      })).catch((e) => {
        setExists(false);
      })

      CSAPIInstance.fetchItemHistory(name)
      .then((val) => {
        if(val != null){

          avg_ = val[0].avg;
          median_ = val[0].median;
          price_ = val[0].price;
          sold_ = val[0].sold;
          createdat_ = val[0].createdat;

          setAvg(avg_);
          setMedian(median_);
          setPrice(price_);
          setSold(sold_);
          setCreatedAt(createdat_);

        }
      })
  }


  return (
    <div className='App'>
      <SearchBar onPress={search} />
      {exists ?
        <>
          <div className="standard-box image-box">
            < img className="image" src={image} />
          </div>
          <text className='header-text'>{itemName}</text>
          <text className='header-text'>${pricemed}</text>
          <text className="header-text">{infoText}</text>
          <text className="header-text">{avg}</text>
          <text className="header-text">{median}</text>
          <text className="header-text">{price}</text>
          <text className="header-text">{sold}</text>
          <text className="header-text">{createdat}</text>
        </>
        : <text className="header-text">Please Search A Valid Skin. Are You Sure The Wear Exists?</text>
      }

    </div>
  );
}

export default App;
